						<?php 					$footer_custom_code =  pegasus_theme_get_option('custom_bottom_textareacode');									if($footer_custom_code){				?>					<section class="clearfix">						<div class="container-fluid">							<div class="row">								<?php echo $footer_custom_code; ?>							</div>						</div>					</section>				<?php } ?>								<section class="pegasus-footer" >									<footer>						<?php 							$full_container_chk_choice =  pegasus_theme_get_option('full_container_chk' ); 							$full_container_footer_choice =  pegasus_theme_get_option('footer_fullwidth_checkbox' ); 						?>						<?php if ( is_active_sidebar( 'footer-social' ) ) : ?>														<div class="<?php if($full_container_chk_choice === 'on') { 													echo 'container-fluid'; 												}else{													echo 'container';												}?>">								<?php dynamic_sidebar( 'footer-social' ); ?>							</div>												<?php endif; ?>												<div class="<?php if($full_container_chk_choice === 'on') { 														echo 'container-fluid'; 													}else{														echo 'container';													}?>">														<div class="row">												<div class="col-xs-12">										<?php 										$hr_check =   pegasus_theme_get_option( 'footer_hr_checkbox'); 										//echo $hr_check;										if($hr_check != 'on'){											echo "<hr>";										}																	?>																		<?php									$footer_widget_areas = absint(  pegasus_theme_get_option( 'footer_widget_areas' ) );									if( $footer_widget_areas ) :										$widget_area_width = 100 / $footer_widget_areas;										?>										<div id="footer-widgets" class="footer-widget-areas clearfix">										<?php for( $i = 1; $i <= $footer_widget_areas; $i++ ): ?>											<div class="footer-widget-area" role="complementary" style="width: <?php echo $widget_area_width; ?>%;">												<?php dynamic_sidebar( 'footer-' . $i ); ?>											</div><!-- .widget-area -->										<?php endfor; ?>																				</div> 									<?php endif; ?>								</div>							</div>						</div><!-- /container -->												<div class="<?php if($full_container_chk_choice === 'on') { 														echo 'container-fluid'; 													}elseif ($full_container_footer_choice === 'on') { 														echo 'container-fluid'; 													}else{														echo 'container';													}?>">														<div class="">									<div id="colophon" class="site-footer" role="contentinfo">									<div class="site-info">										<?php 											$custom_footer =   pegasus_theme_get_option( 'footer_copy_textareacode');											if($custom_footer) {										?>											<?php echo $custom_footer; ?>										<?php }else { ?>											<p class="copyright">&copy; <?php echo date('Y');?> <?php bloginfo('name'); ?></p>										<?php }//end else ?>									</div><!-- .site-info -->								</div><!-- .site-footer -->																																		</div>						</div><!-- /container -->					</footer>				</section>						</div><!--mainbar end-->				</div <!--end-wrapper-->		<?php wp_footer(); ?>			</body></html>
